import styled from 'styled-components'

//Colocar limite de espaço junto a uma barra de rolagem
export default styled.div`
grid-area: SL;
display: flex;
flex-direction: column;
align-items: center;
li{
  list-style: none;
  margin-top: 7px;
}

#unvisible{
  display:none;
}
button{
  width:50%;
  height:15%; 
  align-self: flex-start;
  background-color: var(--secondary);
  border-radius:5px;
  margin-bottom:2px;
}

div.box{
  border:solid 1px gray;
}
div#speechesList{
padding:0;
}
ul{
  display: flex;
flex-direction: column;
  max-height:90%;
  overflow-y: scroll;
    ::-webkit-scrollbar {
    width: 4px;
  }

  ::-webkit-scrollbar-thumb {
    /* background-color: ; */
    border-radius: 4px;
  }
}
div.titulo{
  margin:0;
  background-color: var(--primary);
height:50px;
width:100%;
border-radius: 5px 5px 0px 0px;
}

`
