import React, {useState} from 'react';


const Chronometer: React.FC = () => {
  const [time, setTime] = useState(0);
  const [value, setValue] = useState(10);

  const handleClick = (name: string) => {
    if (name === 'reset') {
      setValue(0);
      return setTime(0);
    }
    if (name === 'less') {
      if (value >= 1) setValue((prevValue) => prevValue - 1);
    } else if (name === 'less5') {
      value >= 5 ? setValue((prevValue) => prevValue - 5) : setValue(0);
    } else if (name === 'more')
      setValue((prevValue) => prevValue + 1);
    else setValue((prevValue) => prevValue + 5);
  };

  const handleInput = (event: React.ChangeEvent<HTMLInputElement>) => {
    event.preventDefault();
    const newValue = +event.target.value;
    setValue(() => newValue);
  };

  const handleChange = () => {
    let counter = 0;
    let timerId = setInterval(() => {
      setTime((prevTime) => prevTime + 1);
      counter++;
      if (counter === value) clearInterval(timerId);
    }, 100);
  };

  return (
    <>
      <div
        className="container h-screen flex flex-col items-center justify-center mx-auto"
      >
        <button
          className="cursor-pointer text-white text-3xl"
          name="reset"
          onClick={()=>handleClick("reset")}
        >
          ❌
        </button>
        <div className="flex justify-center">
          <button
            name="less5"
            onClick={()=>handleClick("less5")}

            className="px-3 text-4xl select-none"
          >
            ⏪
          </button>
          <button
            name="less"
            onClick={()=>handleClick("less")}

            className="px-3 text-4xl select-none"
          >
            ☚
          </button>
          <input
            className="my-4 text-4xl w-1/5 text-center"
            type="text"
            name="input"
            onChange={handleInput}
            value={value}
          />
          <button
            name="more"
            onClick={()=>handleClick("more")}

            className="px-3 text-4xl select-none"
          >
            ☛
          </button>
          <button
            name="more5"
            onClick={()=>handleClick("more5")}

            className="px-3 text-4xl select-none"
          >
            ⏩
          </button>
        </div>
        <span
          className="cursor-pointer text-white text-6xl"
          onClick={handleChange}
        >
          {time}
        </span>
      </div>
    </>
  );
};

export default Chronometer;
