import React, {useState, useEffect} from 'react';


const Chronometer: React.FC = () => {
  const [startStop, setStartStop] = useState('start')
  const [time, setTime] = useState(0)

  function changeTime(){

    if(startStop === "start"){
    setStartStop('stop')
    runTime()

    }else{
    setStartStop('start')
    runTime()
    }
  }
  function runTime(){
    console.log(time)
    if(startStop === 'stop'){
      setTimeout(function(){ 
        setTime(time+1)
    console.log(time)

        runTime()
       }, 1000);
    }
  }
  function reset(){
    setTime(0)
    setStartStop('start')
  }

  return(
    <div>
      <button onClick={changeTime}>{startStop}</button>
      <button onClick={reset}>reset</button>
      <div>
        {time}
      </div>
    </div>
  )
};

export default Chronometer;
