import React, {useState, useEffect} from 'react';


const Chronometer: React.FC = () => {
  const [startStop, setStartStop] = useState('start')
  const [time, setTime] = useState(0)
  const [runStop, setRunStop] = useState(true)
  function changeTime(){
    console.log(startStop)
    if(startStop === "start"){
    setStartStop('stop')
    setRunStop(true)
    console.log(startStop)

    runTime()

    }else{
    setStartStop('start')
    runTime()
    setRunStop(false)

    }
  }
  function runTime(){
    console.log(startStop)
    do{
      let i = time +1
      setTime(i)
    }while(runStop)
  }

  function reset(){
    console.log('reset')
    setTime(0)
    setStartStop('start')
    setRunStop(false)
  }

  return(
    <div>
      <button onClick={changeTime}>{startStop}</button>
      <button onClick={reset}>reset</button>
      <div>
        {time}
      </div>
    </div>
  )
};

export default Chronometer;
