import React, { useState } from 'react'

import { Jutsu } from 'react-jutsu'
import io from 'socket.io-client'

const socket = io('http://localhost:3001')
const user = localStorage.getItem('representation')


const JutsuComponent = ({moderator}) => {
  const [room, setRoom] = useState('')
  const [name, setName] = useState('')
  const [call, setCall] = useState(false)
  const [password, setPassword] = useState('')

  const handleClick = event => {
    event.preventDefault()
    if (room && name) setCall(true)
    socket.emit('setMeet', {room, password})
  }
  socket.on('setMeet', ({room, password})=>{
    alert(room )
    setRoom(room)
    setPassword(password)
    setName(user)
    setCall(true)
  })
  if (moderator){
    return (
      <div>
        {call ? (<Jutsu
          roomName={room}
          password={password}
          displayName={name}
          onMeetingEnd={() => {
            setCall(false)
          }}
          loadingComponent={<p>ʕ •ᴥ•ʔ jitsi is loading ...</p>} />)
          : (
            <form>
              <input id='room' type='text' placeholder='Room' value={room} onChange={(e) => setRoom(e.target.value)} />
              <input id='name' type='text' placeholder='Name' value={name} onChange={(e) => setName(e.target.value)} />
              <input id='password' type='text' placeholder='Password (optional)' value={password} onChange={(e) => setPassword(e.target.value)} />
              <button onClick={handleClick} type='submit'>
                Start / Join
              </button>
            </form>
          )}
      </div>
    )
  }
  return (
    <div>
      {call ? (<Jutsu
        roomName={room}
        password={password}
        displayName={name}
        onMeetingEnd={() => {
          setCall(false)
        }}
        loadingComponent={<p>ʕ •ᴥ•ʔ jitsi is loading ...</p>} />)
        : (
         <div>
           <h2>Esperando a reunião começar...</h2>
         </div>
        )}
    </div>
  )

}

export default JutsuComponent
