import React, {useState, useEffect} from 'react';


const Chronometer: React.FC = () => {
  const [startStop, setStartStop] = useState('start')
  const [time, setTime] = useState('00:00')
  var cronometro : number
  var min = 0
  var seg = 0
  function changeTime(status: string){
   if(status==='start'){
    cronometro = setInterval(()=>{runTime()}, 1000)
    setStartStop('stop')
   }else{
    clearInterval(cronometro)
    setStartStop('start')
    setTime(`${min}:${seg}`)

   }
  }
  function runTime(){
    seg++
    if(seg==60){
      seg = 0
      min++
    }
    console.log(`${min}:${seg}`)
  setTime(`${min}:${seg}`)

  }

  function reset(){
    clearInterval(cronometro)
    min = 0
    seg = 0
    setTime(`${min}:${seg}`)

  }

  return(
    <div>
      <button  onClick={()=>changeTime(startStop)}>{startStop}</button>
      <button onClick={reset}>reset</button>
      <div>
        {time}
      </div>
    </div>
  )
};

export default Chronometer;
