import React, { Component } from 'react'

class Chronometer extends Component {
  constructor (props) {
    super(props)
    this.state = {
      count: 1,
      status: 'start'
    }
  }

  render () {
    const {count} = this.state
    return (
      <div>
        <h1>Current Count: {count}</h1>
        <button onClick={()=>this.doIntervalChange()}>{this.state.status}</button>
        <button onClick={()=>clearInterval(this.myInterval)}>Stop</button>
        <button onClick={()=>this.setState({count: this.props.startCount})}>Reset</button>
      </div>
    )
  }
  // setInterval
  // clearInterval
  componentDidMount () {
    const {startCount} = this.props
    this.setState({
      count: startCount
    })
    // this.doIntervalChange()
  }

  doIntervalChange = () => {
      this.myInterval = setInterval(() => {
      this.setState(prevState => ({
        count: prevState.count - 1
      }))
    }, 1000)
  }

  componentWillUnmount () {
    clearInterval(this.myInterval)
  }
}

export default Chronometer
