import React, {useState} from 'react';


const Chronometer: React.FC = () => {
  const [startStop, setStartStop] = useState('start')
  const [time, setTime] = useState(0)
  var ss = 'start'
  var Time = 0
  function changeTime(){
    console.log(startStop)
    if(startStop === "start"){
    setStartStop('stop')
    ss = 'stop'
    console.log(startStop)

    runTime()

    }else{
    setStartStop('start')
    ss = 'start'
    runTime()
    }
  }
  function runTime(){
    console.log(Time)
    if(ss === 'stop'){
      setTimeout(function(){ 
        setTime(time+1)
        Time++
        console.log(Time)

        runTime()
       }, 1000);
    }else{
      return
    }
  }
  function reset(){
    console.log('reset')
    setTime(0)
    setStartStop('start')
    ss = 'start'
  }

  return(
    <div>
      <button onClick={changeTime}>{startStop}</button>
      <button onClick={reset}>reset</button>
      <div>
        {time}
      </div>
    </div>
  )
};

export default Chronometer;
